CREATE OR REPLACE FUNCTION hashCode(_string text) RETURNS BIGINT AS $$
DECLARE
  val_ CHAR[];
  h_ BIGINT := 0;
  ascii_ BIGINT;
  c_ char;
BEGIN
  val_ = regexp_split_to_array(_string, '');

  FOR i in 1 .. array_length(val_, 1)
  LOOP
    c_ := (val_)[i];
    ascii_ := ascii(c_);
    h_ = (31 * h_ + ascii_ ) % (1e9 + 9);
    raise info '%: % = %', i, c_, h_;
  END LOOP;
RETURN h_;
END;
$$ LANGUAGE plpgsql;
